import { Pipe, PipeTransform } from '@angular/core';

import { Book } from '../models/book.model';

@Pipe({
  name: 'filterByAuthor',
})
export class FilterByAuthorPipe implements PipeTransform {

  public transform(books: Book[], authorId: number): Book[] {
    if (books.length === 0 || authorId === undefined) {
      return books;
    }

    return books.filter(book => book.authorId === authorId);
  }

}

import { Pipe, PipeTransform } from '@angular/core';

import { Book } from '../models/book.model';

@Pipe({
  name: 'filterByGenre',
})
export class FilterByGenrePipe implements PipeTransform {

  public transform(books: Book[], genreId: number): Book[] {
    if (books.length === 0 || genreId === undefined) {
      return books;
    }

    return books.filter(
      (book) => {
        return book.genres.findIndex(
          genre => genre.id === genreId,
        ) !== -1;
      },
    );
  }

}

import { DatePipe } from '@angular/common';
import { Pipe, PipeTransform } from '@angular/core';

import { TranslateService } from '@ngx-translate/core';

@Pipe({
  name: 'localizedDate',
})

export class LocalizedDatePipe implements PipeTransform {

  constructor(
    private readonly translateService: TranslateService,
  ) {}

  public transform(value: string): string {
    return new DatePipe(this.translateService.currentLang)
      .transform(value, 'medium');
  }

}

import { Pipe, PipeTransform } from '@angular/core';

import { Book } from '../models/book.model';

@Pipe({
  name: 'searchText'
})
export class SearchTextPipe implements PipeTransform {

  public transform(books: Book[], searchValue: string): unknown {

    if (
      books.length === 0 ||
      searchValue === undefined ||
      searchValue.length === 0
    ) {
      return books;
    }

    return books.filter(
      (book) => {
        if (
          book.title.toLowerCase().includes(searchValue.toLowerCase()) ||
          book.description.toLowerCase().includes(searchValue.toLowerCase())
        ) {
          return book;
        }
      }
    );
  }

}

