const DEFAULT_SIZE: ISize = {
  width: '400px',
  height: 'auto'
};

@Injectable({
  providedIn: 'root'
})
export class DialogService {

  constructor(
    public dialog: MatDialog
  ) { }

  public openDialog(
    data: IDialogBody,
    size: ISize = DEFAULT_SIZE
  ): Observable<any> {
    const dialogRef = this.dialog.open(DialogComponent, {
      width: size.width,
      height: size.height,
      data
    });

    return dialogRef.afterClosed();
  }

}

