import { Injectable } from '@angular/core';

import { isMatchWith, isEqual } from 'lodash-es';

@Injectable({
  providedIn: 'root'
})
export class HelperService {

  constructor() { }

  public isMatchWith(prev: any, cur: any): boolean {
    return isMatchWith(cur, prev, this._compareNullAndArray);
  }

  private _compareNullAndArray(oldValue: any, newValue: any): boolean {
    if (oldValue === null && Array.isArray(newValue)) {
      if (newValue.length === 0) {
        return true;
      }
    } else if (isEqual(oldValue, newValue) || oldValue === newValue) {
      return true;
    } else if (oldValue === null && newValue.length === 0) {
      return true;
    } else {
      return false;
    }
  }

}

import { Injectable } from '@angular/core';

import { Observable, of } from 'rxjs';

const appearences = [
  'legacy',
  'standard',
  'fill',
  'outline'
];

@Injectable({
  providedIn: 'root'
})
export class MatAppearenceService {

  private _activeAppearence: string;

  constructor() {
    const localAppearence = localStorage.getItem('activeAppearence');

    if (localAppearence !== null) {
      this._activeAppearence = localAppearence;
    } else {
      localStorage.setItem('activeAppearence', appearences[0]);
      this._activeAppearence = appearences[0];
    }
  }

  public getAppearences(): Observable<string[]> {
    return of(appearences);
  }

  public systemAppearence(): Object {
    return {
      appearance: this._activeAppearence
    };
  }

  public getActiveAppearance(): string {
    return this._activeAppearence;
  }

  public setActiveAppearance(appearence: string): void {
    localStorage.setItem('activeAppearence', appearence);

    this._activeAppearence = appearence;
  }

}

import { Injectable } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';

import { Observable, BehaviorSubject } from 'rxjs';
import { take } from 'rxjs/operators';

import { IFilterParam } from '../models/filter-param.interface';

@Injectable({
  providedIn: 'root'
})
export class ParamsService {

  private _curParams: IFilterParam = {
    page: 1
  };
  private _curParams$ = new BehaviorSubject<IFilterParam>(this._curParams);

  constructor(
    private readonly router: Router,
    private readonly route: ActivatedRoute
  ) {
    this._listenQueryParams();
  }

  public setNewParams(params: Object): void {
    const paramKeys = Object.keys(params);

    paramKeys.forEach((key) => {
      switch (key) {
        case 'searchText':
          if (params['searchText'] && params['searchText'].length > 0) {
            this._curParams.searchText = params['searchText'];
          } else {
            delete this._curParams.searchText;
          }

          break;

        case 'genreNames':
          if (params['genreNames'] && params['genreNames'].length > 0) {
            if (Array.isArray(params['genreNames'])) {
              this._curParams.genreNames = params['genreNames'];
            } else {
              this._curParams.genreNames = Array(params['genreNames']);
            }
          } else {
            delete this._curParams.genreNames;
          }

          break;

        case 'authorIds':
          if (params['authorIds'] && params['authorIds'].length > 0) {
            let authorIds = [];

            if (Array.isArray(params['authorIds'])) {
              authorIds = params['authorIds'];
            } else {
              authorIds = Array(params['authorIds']);
            }

            this._curParams.authorIds = authorIds
              .map((strId: string) => parseInt(strId, 0));
          } else {
            delete this._curParams.authorIds;
          }

          break;

        case 'page':
          this._curParams.page = parseInt(params['page'], 0);

          break;
      }
    });
    this._curParams$.next(this._curParams);
    this._setTree();
  }

  public getParams(): IFilterParam {
    return this._curParams;
  }

  public getParams$(): Observable<IFilterParam> {
    return this._curParams$.asObservable();
  }

  private _listenQueryParams(): void {
    this.route.queryParamMap
      .pipe(take(1))
      .subscribe(
        params => this.setNewParams(params['params'])
      );
  }

  private _setTree(): void {
    const routeParams = {
      relativeTo: this.route,
      replaceUrl: true,
      queryParams: this.getParams(),
    };

    this.router.navigate([], routeParams);
  }

}

import { Injectable } from '@angular/core';
import { HttpParams } from '@angular/common/http';

import { Ransack } from '../models/ransack.enum';
import { IRansackParam } from '../models/ransack-param.interface';

@Injectable({
  providedIn: 'root'
})
export class RansackService {


  constructor() { }

  /**
    * Filter keys must be same with Options keys
    *
    * For example:
    *
    * filters: {
    *   authorId: [1,2,3]
    * }
    *
    * options: {
    *   authorId: {
    *     matcher: Ransack.In
    *   }
    * }
  */

  /**
    * Filter can take 3 types of Input params:
    * Ransack enum operation
    * Object of IRansackParam
    * Array of objects IRansackParam
    *
    * For example
    *
    * {
    *   authorId: {
    *     matcher: Ransack.In,
    *   },
    *   price: [
    *     {
    *       matcher: Ransack.Gt,
    *       from: 'min'
    *    },
    *    {
    *       matcher: Ransack.Lt,
    *       from: 'max'
    *    }
    *  ],
    *  title: Ransack.NotEq,
    *}
  */

  /**
    * Options params:
    *
    ****
    * matcher - choose ransack operation;

    ****
    * postfix - add postfix name to param, for example:
    *
    * option = {
    *   author: {
    *     postfix: 'id'
    *   }
    * }
    *
    * return: q[author_id_{matcher}]
    *
    ****
    * name - change naming ransack params from object key to custom name, 
    * for example:
    *
    * options = {
    *   authorId: {
    *     name: 'authorIds'
    *   }
    * };
    *
    * return: q[author_ids_{matcher}]
    *
    ****
    * from - choose value of param from input Object by key, for example:
    *
    * filters = {
    *   price: {
    *     min: 1,
    *     max: 10
    *   }
    * };
    *
    * options = {
    *   price: {
    *     mathcer: Ransack.Gt,
    *     from: 'min'
    *   }
    * }
    *
    * return: 'q[price_gt] = 1'
  */

  public toRansack(filters: any, options?: any): HttpParams {
    let httpParams = new HttpParams();

    const keys = Object.keys(filters);
    keys.forEach((key) => {
      if (Array.isArray(options[key])) {
        /** If Options is Array of options */

        options[key].forEach((option) => {
          httpParams = this._setParam(option, filters, key, httpParams);
        });
      } else if (options[key] instanceof Object) {
        /** If Options is Option object */

        const option = options[key];
        httpParams = this._setParam(option, filters, key, httpParams);
      } else {
        /** If Options is Ransack operation */

        const option = {
          matcher: options[key]
        };

        httpParams = this._setParam(option, filters, key, httpParams);
      }
    });

    return httpParams;
  }

  private _setParam(
    option: IRansackParam,
    filters: any,
    key: string,
    httpParams: HttpParams
  ): HttpParams {
    let filter = filters[key];

    const matcher = this._kebabStr(Ransack[option.matcher]);

    const name = option.hasOwnProperty('name') ?
      this._kebabStr(option.name) : this._kebabStr(key);

    const postfix = option.hasOwnProperty('postfix') ?
      `_${option.postfix}` : '';

    if (option.hasOwnProperty('from')) {
      filter = filter[option.from];
    }

    let paramName = `q[${name}${postfix}_${matcher}]`;

    if (Array.isArray(filter)) {
      paramName = paramName + '[]';

      filter.forEach((filterEl) => {
        httpParams = httpParams.set(paramName, filterEl);
      });
    } else {
      httpParams = httpParams.set(paramName, filter);
    }

    return httpParams;
  }

  private _kebabStr(str: string): string {
    return str.split(/(?=[A-Z])/)
      .map((el) => el.toLowerCase())
      .join('_');
  }
}

