@Component({
  selector: 'app-book-edit',
  templateUrl: './book-edit.container.html',
  styleUrls: ['./book-edit.container.sass']
})
export class BookEditContainer implements OnInit, OnDestroy {

  public bookForm: FormGroup;
  public book: Book;
  public matcher = new BookErrorStateMatcher();

  public imageSrc: string;
  public submited = false;
  public edited = false;
  public fileUrl: string | ArrayBuffer;

  public authors$: Observable<Author[]>;
  public genres$: Observable<Genre[]>;

  private _destroy$ = new Subject<void>();

  constructor(
    private readonly snack: MatSnackBar,
    private readonly route: ActivatedRoute,
    private readonly router: Router,
    private readonly booksService: BooksService,
    private readonly genresService: GenresService,
    private readonly authorsService: AuthorsService,
    private readonly fb: FormBuilder,
    private readonly dialogService: DialogService,
    private readonly compressService: NgxImageCompressService,
    private readonly validator: AppValidator
  ) { }

  public ngOnInit(): void {
    this.book = this.route.snapshot.parent.data.book;

    if (this.book.image) {
      this.imageSrc = environment.hosts.imgHost + this.book.image;
    }

    this.getAuthors();
    this.getGenres();
    this.loadForm();
  }

  public ngOnDestroy(): void {
    this._destroy$.next();
    this._destroy$.complete();
  }

  public onSubmit(cf: IForm): void {
    this.submited = true;

    if (!this.bookForm.invalid) {
      const bookRequest = BookRequest.new(BookRequest, cf);

      this.booksService.updateBook(bookRequest)
        .pipe(takeUntil(this._destroy$))
        .subscribe(() => this._snackMessage());
    }
  }

  public canDeactivate(): boolean | Observable<boolean> | Promise<boolean> {
    if (this.edited && !this.submited) {
      const data: IDialogBody = {
        message: 'Are you sure you want to leave form?',
        type: 'multiple'
      };

      return new Promise((resolve) => {
        const close = this.dialogService.openDialog(data)
          .pipe(
            take(1),
            takeUntil(this._destroy$)
          )
          .subscribe(
            res => resolve(res)
          );
      });
    }

    return true;
  }

  public loadForm(): void {
    this.bookForm = this.fb.group(
      {
        id: [ this.book.id, Validators.required],
        title: [ this.book.title, Validators.required],
        description: [this.book.description, Validators.required],
        author: [this.book.authorId, Validators.required],
        genres: [this.book.genres],
        writingDate: [this.book.writingDate, Validators.required],
        releaseDate: [this.book.releaseDate, Validators.required],
        price: [this.book.price, Validators.required],
        image: [null],
        previews: [null],
        uploadedImage: false,
      },
      {
        validators: [
          this.validator.checkDateValidation,
          this.validator.checkImageValidation]
      }
    );
    if (this.book.image) {
      this.bookForm.patchValue({
        uploadedImage: true
      });
    }
    this.bookForm.valueChanges
      .pipe(takeUntil(this._destroy$))
      .subscribe(() => this.edited = true);
  }

  public upload(fileList: FileList): void {
    const file = fileList[0];
    const reader = new FileReader();
    const filename = file.name;
    const orientation = -1;

    reader.addEventListener('load', (event: Event) => {
      const localUrl = event.target['result'];
      this.fileUrl = `url('${localUrl}')`;

      this.compressService
        .compressFile(localUrl, orientation, 50, 50)
        .then(
          (base64) => {
            fetch(base64)
              .then(res => res.blob())
              .then((blob) => {
                const compressedFile = new File(
                  [blob],
                  filename,
                  { type: 'image/png' }
                );

                this.bookForm.patchValue({
                  image: compressedFile,
                  uploadedImage: true
                });
              });
          }
        );
    });
    reader.readAsDataURL(file);
  }

  public getGenres(): void {
    this.genres$ = this.genresService
      .getAllGenres();
  }

  public getAuthors(): void {
    this.authors$ = this.authorsService
      .getAllAuthors();
  }

  private _snackMessage(): void {
    this.snack.open('Book edited', 'Ok', {
      duration: 3000,
      horizontalPosition: 'end',
      verticalPosition: 'top'
    });

    this.router.navigate(['/books']);
  }

}

import { Component, OnInit, OnDestroy } from '@angular/core';
import { FormGroup, FormBuilder } from '@angular/forms';

import { Subject, Observable } from 'rxjs';
import { takeUntil, debounceTime } from 'rxjs/operators';

import { HelperService } from '../../../core/services/helper.service';
import { AuthorsService } from '../../../core/services/authors.service';
import { GenresService } from '../../../core/services/genres.service';
import { ParamsService } from '../../services/params.service';
import { Author } from '../../../authors/models/author.model';
import { Genre } from '../../../genres/models/genre.model';
import { IFilterParam } from '../../models/filter-param.interface';

const DEFAULT: IFilterParam = {
  searchText: null,
  genreNames: null,
  authorIds: null
};

const DEFAULT_MATRIX_OF_CHANGES = [true];

@Component({
  selector: 'app-book-filter',
  templateUrl: './book-filter.container.html',
  styleUrls: ['./book-filter.container.sass']
})
export class BookFilterContainer implements OnInit, OnDestroy {

  public disabled = true;
  public openedFilters = false;

  public prevMatrixChanges = DEFAULT_MATRIX_OF_CHANGES;
  public filterForm: FormGroup;

  public authors$: Observable<Author[]>;
  public genres$: Observable<Genre[]>;

  private _destroy$ = new Subject<void>();
  private _prevParams: IFilterParam;

  constructor(
    private readonly fb: FormBuilder,
    private readonly authorsService: AuthorsService,
    private readonly genresService: GenresService,
    private readonly qParams: ParamsService,
    private readonly helper: HelperService
  ) {
    this._getParams();
  }

  public ngOnInit(): void {
    this.getGenres();
    this.getAuthors();
  }

  public ngOnDestroy(): void {
    this._destroy$.next();
    this._destroy$.complete();
  }

  public getGenres(): void {
    this.genres$ = this.genresService
      .getAllGenres();
  }

  public getAuthors(): void {
    this.authors$ = this.authorsService
      .getAllAuthors();
  }

  public clearFilter(): void {
    this.disabled = true;
    this.qParams.setNewParams(DEFAULT);
    this._setNullParams();
  }

  public toggleFilters(): void {
    this.openedFilters = !this.openedFilters;
  }

  private _setValueChanges(): void {
    this.filterForm.valueChanges
      .pipe(
        debounceTime(1000),
        takeUntil(this._destroy$)
      )
      .subscribe((res: IFilterParam) => {
        this.disabled = false;

        const isMatch = this.helper.isMatchWith(res, this._prevParams);
        this._prevParams = res;

        if (!isMatch) {
          this.qParams.setNewParams(res);
          this.qParams.setNewParams({ page: 1 });
        }
      });
  }

  private _getParams(): void {
    this.qParams.getParams$()
      .pipe(takeUntil(this._destroy$))
      .subscribe(
        (res) => {
          this._initForm(res);
          this._setNullParams();
          Object.assign(this._prevParams, res);
          delete this._prevParams.page;
          this.disabled = false;
        }
      );
  }


  private _initForm(params: IFilterParam): void {
    this.filterForm = this.fb.group({
      searchText: [params.searchText],
      authorIds: [params.authorIds],
      genreNames: [params.genreNames]
    });

    this._setValueChanges();
  }

  private _setNullParams(): void {
    this._prevParams = {
      searchText: null,
      genreNames: null,
      authorIds: null
    };
  }

}

