    params: IFilterParam
  ): void {
    this.loaded = false;
    this.booksService.getBooks(params)
      .pipe(
        delay(700),
        takeUntil(this._destroy$)
      )
      .subscribe(
        (res) => {
          this.loaded = true;
          this.books = res.books;
          this.countRecords = res.meta.records;
          this.countPages = res.meta.pages;
        }
      );
  }

