  constructor(
    private readonly router: Router
  ) {
    this.router.events
      .pipe(takeUntil(this._destroy$))
      .subscribe((event) => {
        if (event instanceof NavigationEnd) {
          if (event.url.includes('create')) {
            this.isHidden = true;
          } else {
            this.isHidden = false;
          }
        }
      });
  }

  public ngOnInit(): void {}

  public openCreate(): void {
    this.router.navigate(['/genres/create']);
  }

